function App() {
  const [walletAddress, setWalletAddress] = useState('');
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleConnect = async (address) => {
    try {
      setLoading(true);
      setWalletAddress(address);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <Router>
      <div className="min-h-screen bg-gradient-to-r from-purple-400 to-blue-500 p-4" style={{ fontFamily: 'Bubblegum Sans, cursive' }}>
        <div className="max-w-4xl mx-auto bg-white rounded-lg shadow-lg overflow-hidden relative">
          <Navbar />

          {error && (
            <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded relative" role="alert">
              <span className="block sm:inline">{error}</span>
              <button onClick={() => setError(null)} className="absolute top-0 right-0 px-4 py-3">
                <span className="sr-only">Close</span>
                √ó
              </button>
            </div>
          )}

          <div className="flex justify-center p-4">
            <WalletConnect onConnect={handleConnect} isLoading={loading} />
          </div>

          {walletAddress && (
            <p className="text-lg text-center text-gray-700 mb-4">
              Connected Wallet: {walletAddress}
            </p>
          )}

          <main className="p-8 text-center">
            <h1 className="text-6xl font-bold mb-6 text-purple-800 drop-shadow-lg">
              Monthly NFT Rewards Program üéâ
            </h1>

            <div className="bg-yellow-50 border-2 border-yellow-200 rounded-lg p-4 mb-8 mx-auto max-w-2xl">
              <p className="text-yellow-800 font-medium">
                ‚ö†Ô∏è Please Note: The actual reward amounts will be proportional to the number of NFTs sold.
              </p>
            </div>

            <div className="mb-8">
              <h2 className="text-3xl font-bold mb-4 text-purple-800">Monthly Prize Pool: $3,750 üèÜ</h2>
              <div className="space-y-2">
                <p>üêº üèÜ Grand Prize: $1,500</p>
                <p>üêº üèÜ Second Prize: $1,000</p>
                <p>üêº üèÜ Third Prize: $750</p>
                <p>üêº üèÜ Fourth Prize: $300</p>
                <p>üêº üèÜ Fifth Prize: $200</p>
              </div>
            </div>

            <div className="mb-8">
              <h2 className="text-3xl font-bold mb-4 text-purple-800">How It Works üéØ</h2>
              <div className="space-y-2">
                <p>Every month, 5 lucky NFT holders will be randomly selected</p>
                <p>Odds of winning Grand Prize: 1 in 10,000</p>
                <p>Odds of winning Second Prize: 1 in 10,000</p>
                <p>Odds of winning Third Prize: 1 in 10,000</p>
                <p>Odds of winning Fourth Prize: 1 in 10,000</p>
                <p>Odds of winning Fifth Prize: 1 in 10,000</p>
                <p>Winners have 14 days to claim their prizes</p>
                <p>Unclaimed prizes will be added to next month's drawing</p>
              </div>
            </div>

            <div className="mb-8">
              <h2 className="text-3xl font-bold mb-4 text-purple-800">Prize Distribution üí∞</h2>
              <div className="text-center">
                For each prize won:
                <div className="space-y-2 mt-2">
                  <p>50% will be added to the winning NFT's liquidity</p>
                  <p>50% will be airdropped to the winning wallet in stablecoin</p>
                </div>
              </div>
            </div>
          </main>

          <Suspense fallback={<div className="text-center p-4">Loading...</div>}>
            <Routes>
              <Route path="/" element={<Home walletAddress={walletAddress} />} />
              <Route path="/winners" element={<Winners />} />
              <Route path="/past-winners" element={<PastWinners />} />
            </Routes>
          </Suspense>

          <Footer />

          {/* Enhanced Confetti Elements */}
          {[...Array(9)].map((_, index) => (
            <div
              key={`confetti-${index}`}
              className="confetti"
              style={{
                left: `${(index + 1) * 10}%`,
                animationDelay: `${index * 0.5}s`,
                backgroundColor: ['#FF69B4', '#87CEEB', '#98FB98', '#DDA0DD', '#F0E68C'][index % 5]
              }}
              aria-hidden="true"
            />
          ))}
        </div>
      </div>
    </Router>
  );
}

export default App;  // Add this line if it's missing
