// src/components/Home.js  
import React, { useState, useEffect } from 'react';  
  
const Home = ({ walletAddress }) => {  
  const [timeLeft, setTimeLeft] = useState({  
    days: 0,  
    hours: 0,  
    minutes: 0,  
    seconds: 0,  
  });  
  
  useEffect(() => {  
    const nextDrawDate = new Date('2025-01-15T00:00:00'); // Example date - adjust as needed  
  
    const updateTimer = () => {  
      const now = new Date();  
      const difference = nextDrawDate - now;  
  
      if (difference <= 0) {  
        clearInterval(timer);  
        setTimeLeft({ days: 0, hours: 0, minutes: 0, seconds: 0 });  
        return;  
      }  
  
      const days = Math.floor(difference / (1000 * 60 * 60 * 24));  
      const hours = Math.floor((difference / (1000 * 60 * 60)) % 24);  
      const minutes = Math.floor((difference / 1000 / 60) % 60);  
      const seconds = Math.floor((difference / 1000) % 60);  
  
      setTimeLeft({ days, hours, minutes, seconds });  
    };  
  
    const timer = setInterval(updateTimer, 1000);  
    updateTimer();  
  
    return () => clearInterval(timer);  
  }, []);  
  
  return (  
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100">  
      <div className="p-8 bg-white rounded-lg shadow-lg">  
        <h1 className="text-4xl font-bubblegum text-center mb-6">Polygon Panda Party</h1>  
        <div className="grid grid-cols-4 gap-4 text-center mb-8">  
          {/* Countdown Timer */}  
          <div className="bg-purple-100 p-4 rounded-lg">  
            <div className="text-4xl font-bold text-purple-600">{timeLeft.days}</div>  
            <div className="text-purple-500">Days</div>  
          </div>  
          <div className="bg-purple-100 p-4 rounded-lg">  
            <div className="text-4xl font-bold text-purple-600">{timeLeft.hours}</div>  
            <div className="text-purple-500">Hours</div>  
          </div>  
          <div className="bg-purple-100 p-4 rounded-lg">  
            <div className="text-4xl font-bold text-purple-600">{timeLeft.minutes}</div>  
            <div className="text-purple-500">Minutes</div>  
          </div>  
          <div className="bg-purple-100 p-4 rounded-lg">  
            <div className="text-4xl font-bold text-purple-600">{timeLeft.seconds}</div>  
            <div className="text-purple-500">Seconds</div>  
          </div>  
        </div>  
        {/* Prize Information */}  
        <div className="mb-8">  
          <h2 className="text-2xl font-bubblegum mb-4">Monthly Prize Pool: $3,750 üèÜ</h2>  
          <div className="space-y-2">  
            <p>üêº üèÜ Grand Prize: $1,500</p>  
            <p>üêº üèÜ Second Prize: $1,000</p>  
            <p>üêº üèÜ Third Prize: $750</p>  
            <p>üêº üèÜ Fourth Prize: $300</p>  
            <p>üêº üèÜ Fifth Prize: $200</p>  
          </div>  
        </div>  
        {/* How it Works */}  
        <div className="mb-8">  
          <h2 className="text-2xl font-bubblegum mb-4">How It Works üéØ</h2>  
          <div className="space-y-2">  
            <p>Every month, 10 lucky NFT holders will be randomly selected</p>  
            <p>Odds of winning Grand Prize: 1 in 10,000</p>  
            <p>Odds of winning Second Prize: 1 in 10,000</p>  
            <p>Odds of winning Third Prize: 1 in 10,000</p>  
            <p>Odds of winning Fourth Prize: 1 in 10,000</p>  
            <p>Odds of winning Fifth Prize: 1 in 10,000</p>  
            <p>Winners have 14 days to claim their prizes</p>  
            <p>Unclaimed prizes will be added to next month's drawing</p>  
          </div>  
        </div>  
        {/* Prize Distribution */}  
        <div className="mb-8">  
          <h2 className="text-2xl font-bubblegum mb-4">Prize Distribution üí∞</h2>  
          <div className="text-center">  
            For each prize won:  
            <div className="space-y-2 mt-2">  
              <p>50% will be added to the winning NFT's liquidity</p>  
              <p>50% will be airdropped to the winning wallet in stablecoin</p>  
            </div>  
          </div>  
        </div>  
        {walletAddress && (  
          <p className="text-lg mb-4">Connected Wallet: {walletAddress}</p>  
        )}  
      </div>  
    </div>  
  );  
};  
  
export default Home;
