import React, { useState, useEffect, Suspense } from 'react';  
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';  
import Home from './components/Home';  
import Winners from './components/Winners';  
import PastWinners from './components/PastWinners';  
import Navbar from './components/Navbar';  
import Footer from './components/Footer';  
import WalletConnect from './components/WalletConnect';  
import './index.css'; // Import your CSS file  
  
function App() {  
  const [walletAddress, setWalletAddress] = useState('');  
  const [isLoading, setIsLoading] = useState(false);  
  const [error, setError] = useState(null);  
  
  const handleConnect = async (address) => {  
    try {  
      setIsLoading(true);  
      setError(null);  
      setWalletAddress(address);  
    } catch (err) {  
      setError(err.message);  
      console.error('Wallet connection error:', err);  
    } finally {  
      setIsLoading(false);  
    }  
  };  
  
  return (  
    <Router>  
      <div className="min-h-screen bg-gradient-to-r from-purple-400 to-blue-500 p-4">  
        <div className="max-w-4xl mx-auto bg-white rounded-lg shadow-lg overflow-hidden relative">  
          <Navbar />  
          <div className="p-8">  
            {error && (  
              <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded relative mb-4" role="alert">  
                <strong className="font-bold">Error:</strong>  
                <span className="block sm:inline">{error}</span>  
              </div>  
            )}  
            <WalletConnect onConnect={handleConnect} isLoading={isLoading} />  
          </div>  
          <Suspense fallback={<div className="text-center p-4">Loading...</div>}>  
            <Routes>  
              <Route path="/" element={<Home walletAddress={walletAddress} />} />  
              <Route path="/winners" element={<Winners />} />  
              <Route path="/past-winners" element={<PastWinners />} />  
              <Route path="/about" element={<About />} />  
            </Routes>  
          </Suspense>  
          <Footer />  
        </div>  
      </div>  
    </Router>  
  );  
}  
  
export default App;
