// App.js
function App() {
  const [walletAddress, setWalletAddress] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleConnect = async (address) => {
    try {
      setIsLoading(true);
      setError(null);
      setWalletAddress(address);
    } catch (err) {
      setError(err.message);
      console.error('Wallet connection error:', err);
    } finally {
      setIsLoading(false);
    }
  };

  const renderConfetti = () => {
    return Array.from({ length: 9 }).map((_, index) => (
      <div
        key={`confetti-${index}`}
        className="confetti"
        style={{
          left: `${(index + 1) * 10}%`,
          animationDelay: `${index * 0.5}s`,
          backgroundColor: ['#FF69B4', '#87CEEB', '#98FB98', '#DDA0DD', '#F0E68C'][index % 5]
        }}
        aria-hidden="true"
      />
    ));
  };

  return (
    <Router>
      <div className="min-h-screen bg-gradient-to-r from-purple-400 to-blue-500 p-4">
        <div className="max-w-4xl mx-auto bg-white rounded-lg shadow-lg overflow-hidden relative">
          <Navbar />

          <div className="p-8">
            {error && (
              <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded relative mb-4" role="alert">
                <strong className="font-bold">Error:</strong>
                <span className="block sm:inline">{error}</span>
              </div>
            )}
            <WalletConnect onConnect={handleConnect} isLoading={isLoading} />
          </div>

          <main className="p-8 text-center">
            <Routes>
              <Route path="/" element={<Home walletAddress={walletAddress} />} />
              <Route path="/winners" element={<Winners />} />
              <Route path="/past-winners" element={<PastWinners />} />
            </Routes>
          </main>

          <Footer />
          {renderConfetti()}
        </div>
      </div>
    </Router>
  );
}

// Navbar Component
function Navbar() {
  return (
    <nav className="bg-gray-800 p-4 relative z-50">
      <div className="max-w-4xl mx-auto flex flex-col items-center">
        <h1 className="text-5xl font-bubblegum text-purple-400 font-bold mb-6">
          Polygon Panda Party
        </h1>
        <div className="flex space-x-4">
          <Link to="/winners">
            <button className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600">
              üèÜ View Current Winners
            </button>
          </Link>
          <Link to="/past-winners">
            <button className="bg-purple-500 text-white px-4 py-2 rounded hover:bg-purple-600">
              üìú View Past Winners
            </button>
          </Link>
        </div>
      </div>
    </nav>
  );
}

// Home Component
function Home({ walletAddress }) {
  const [timeLeft, setTimeLeft] = useState({
    days: 0,
    hours: 0,
    minutes: 0,
    seconds: 0
  });

  useEffect(() => {
    const nextDrawDate = new Date('2024-12-31T00:00:00');

    const updateTimer = () => {
      const now = new Date();
      const difference = nextDrawDate - now;

      const days = Math.floor(difference / (1000 * 60 * 60 * 24));
      const hours = Math.floor((difference / (1000 * 60 * 60)) % 24);
      const minutes = Math.floor((difference / 1000 / 60) % 60);
      const seconds = Math.floor((difference / 1000) % 60);

      setTimeLeft({ days, hours, minutes, seconds });
    };

    const timer = setInterval(updateTimer, 1000);
    updateTimer();

    return () => clearInterval(timer);
  }, []);

  return (
    <div className="space-y-8">
      <div className="bg-purple-100 rounded-lg p-6 shadow-lg">
        <h2 className="text-3xl font-bubblegum mb-6">Next Draw Countdown</h2>
        <div className="grid grid-cols-4 gap-4">
          {[
            { value: timeLeft.days, label: 'Days' },
            { value: timeLeft.hours, label: 'Hours' },
            { value: timeLeft.minutes, label: 'Minutes' },
            { value: timeLeft.seconds, label: 'Seconds' }
          ].map((item, index) => (
            <div key={index} className="bg-white p-4 rounded-lg shadow">
              <div className="text-4xl font-bold text-purple-600">{item.value}</div>
              <div className="text-purple-500">{item.label}</div>
            </div>
          ))}
        </div>
      </div>

      {/* Prize Pool Section */}
      <div className="space-y-6">
        <h2 className="text-3xl font-bubblegum">Monthly Prize Pool: $3,750 üèÜ</h2>
        <div className="space-y-2">
          <p>üêº üèÜ Grand Prize: $1,500</p>
          <p>üêº üèÜ Second Prize: $1,000</p>
          <p>üêº üèÜ Third Prize: $750</p>
          <p>üêº üèÜ Fourth Prize: $300</p>
          <p>üêº üèÜ Fifth Prize: $200</p>
        </div>
      </div>

      {/* How It Works Section */}
      <div className="space-y-6">
        <h2 className="text-3xl font-bubblegum">How It Works üéØ</h2>
        <div className="space-y-2">
          <p>Every month, 5 lucky NFT holders will be randomly selected</p>
          <p>Winners have 14 days to claim their prizes</p>
          <p>Unclaimed prizes will be added to next month's drawing</p>
        </div>
      </div>

      {/* Prize Distribution Section */}
      <div className="space-y-6">
        <h2 className="text-3xl font-bubblegum">Prize Distribution üí∞</h2>
        <div className="space-y-2">
          <p>50% will be added to the winning NFT's liquidity</p>
          <p>50% will be airdropped to the winning wallet in stablecoin</p>
        </div>
      </div>
    </div>
  );
}

// Footer Component
function Footer() {
  return (
    <footer className="bg-gray-800 text-white p-4 text-center mt-auto">
      <p>¬© 2024 Polygon Panda Party. All rights reserved.</p>
    </footer>
  );
}

// Main App Export
export default App;
